document.addEventListener("DOMContentLoaded", function () {
var email_id = document.getElementById("email_id");
var mobile_no = document.getElementById("mobile_no");
var other_details_mobile_no = document.getElementById("other_details_mobile_no");

email_id.oninvalid = function (event) {
event.target.setCustomValidity("Invalid Email Address Format!");
};
mobile_no.oninvalid = function (event) {
event.target.setCustomValidity("Invalid Phone Number Format!");
};
other_details_mobile_no.oninvalid = function (event) {
event.target.setCustomValidity("Invalid Phone Number Format!");
};
email_id.oninput = function (event) {
event.target.setCustomValidity("");
};
mobile_no.oninput = function (event) {
event.target.setCustomValidity("");
};
other_details_mobile_no.oninput = function (event) {
event.target.setCustomValidity("");
};
});

document.addEventListener("DOMContentLoaded", function () {
var panInput = document.getElementById("pan_pattern");

panInput.oninvalid = function (event) {
event.target.setCustomValidity("Invalid PAN Number Format!");
};

panInput.oninput = function (event) {
event.target.setCustomValidity("");
};

// Script to update file name on upload
var fileInput = document.getElementById('file-input');
var fileLabel = document.getElementById('file-label');

fileInput.addEventListener('change', function () {
if (fileInput.files.length > 0) {
fileLabel.innerText = fileInput.files[0].name;
}
});
});

