document.addEventListener("DOMContentLoaded", function() {
var form = document.querySelector('form');
form.addEventListener('submit', function(event) {
var accountNumber = document.getElementById('account_number_id').value;
var verifyAccountNumber = document.getElementById('varify_account_number_id').value;

var OtherAccountNumber = document.getElementById('other_account_number_id').value;
var OtherVerifyAccountNumber = document.getElementById('other_varify_account_number_id').value;

if (accountNumber !== verifyAccountNumber || OtherAccountNumber !== OtherVerifyAccountNumber) {
event.preventDefault();
document.getElementById('account_number_validation').style.display = 'block';
}
});

var fileInput = document.getElementById('file-input');
var other_file_input = document.getElementById('other-file-input');
var fileLabel = document.getElementById('upload_cheque');
var other_upload_cheque = document.getElementById('other_upload_cheque');

fileInput.addEventListener('change', function () {
if (fileInput.files.length > 0) {
fileLabel.innerText = fileInput.files[0].name;
}
});

other_file_input.addEventListener('change', function () {
if (other_file_input.files.length > 0) {
other_upload_cheque.innerText = other_file_input.files[0].name;
}
});
});
